package JavaVoiceMenu.runtime;

/*Generated by MPS */

import java.io.IOException;

public class myVoice {
  public String Samantha = "Say -v Samantha ";
  public String Alex = "Say ";
  public String text;
  public Boolean voice = false;
  /*package*/ myVoice() {
    text = getVoice();
  }
  public myVoice addText(String text) {
    this.text += text + ", ";
    return this;
  }
  public void speak() throws IOException {
    Runtime.getRuntime().exec(text);
    text = getVoice();
  }
  public void changeVoice() {
    voice = !(voice);
    System.out.println(getVoice());
  }
  private String getVoice() {
    if (!(voice)) {
      return Samantha;
    } else {
      return Alex;
    }
  }
}
